-    public Address relocateObject(ZForwarding forwarding, Address o) {
-        Address toAddr = forwardingFind(forwarding, o);
-        if (toAddr != null) {
-            // Already relocated.
-            return toAddr;
-        } else {
-            // Return original address because it is not yet relocated.
-            return o;
-        }
-    }
