+  //
+  //      add_value:      R1 (High), R0 (Low)
+  //      dest:           R2
+  //
+  // Results:
+  //
+  //     R0:R1: the new value stored in dest
+  //
+  // Overwrites:
+  //
+  //     R1, R2, R3, R4
+  //
+  address generate_atomic_add_long() {
+    address start;
+
+    StubCodeMark mark(this, "StubRoutines", "atomic_add_long");
+    Unimplemented();
+    __ bx(LR);
+
+    return start;
+  }
