+// partitioning functions
+template <typename T>
+void avx512_dual_pivot_partition(T *arr, int64_t from_index, int64_t to_index, int32_t *pivot_indices){
+    const int64_t pidx1 = pivot_indices[0];
+    const int64_t pidx2 = pivot_indices[1];
