+    std::swap(arr[pidx1], arr[low]);
+    std::swap(arr[pidx2], arr[end]);
+
+
+    const int64_t pivot_index2 = vectorized_partition<zmm_vector<T>, T>(arr, start, end, pivot2, true); // use_gt = true
