-        }
+        Arrays.fill(ba, (byte) 0x7A);
@@ -131,0 +120,1 @@
+
@@ -132,0 +122,2 @@
+
+                writeLock.lock();
@@ -133,0 +125,5 @@
+                // This isn't in a try/finally. If an exception is thrown
+                // and the lock is released, the main thread will
+                // loop until the test times out. So don't release it.
+                writeLock.unlock();
+
