+// Biggest detectable factor of the invariant.
+int VPointer::invar_factor() {
+  Node* n = invar();
+  if (n == nullptr) {
+    return 0;
+  }
+  int opc = n->Opcode();
+  if (opc == Op_LShiftI && n->in(2)->is_Con()) {
+    return 1 << n->in(2)->get_int();
+  } else if (opc == Op_LShiftL && n->in(2)->is_Con()) {
+    return 1 << n->in(2)->get_int();
+  }
+  // All our best-effort has failed.
+  return 1;
+}
