+     */
+    public static int getTypeSizeInBytes(String typeString) {
+        return switch (typeString) {
+            case TYPE_BYTE              -> 1;
+            case TYPE_CHAR, TYPE_SHORT  -> 2;
+            case TYPE_INT, TYPE_FLOAT   -> 4;
+            case TYPE_LONG, TYPE_DOUBLE -> 8;
+            default                     -> 0;
+        };
+    }
