+            String output = baos.toString().strip();
+            // Example output:
+            // subject= /UID=ABCDABCD/CN=jpackage.openjdk.java.net (\xC3\xB6) (ABCDABCD)/C=US
+            // We need 'CN' value
+            String [] pairs = output.split("/");
+            for (String pair : pairs) {
+                if (pair.startsWith("CN=")) {
