+
+    /**
+     * Models a constant-valued element
+     *
+     * @since 22
+     */
+    @PreviewFeature(feature = PreviewFeature.Feature.CLASSFILE_API)
+    sealed interface OfByte extends AnnotationValue.OfConstant
+            permits AnnotationImpl.OfByteImpl {
+        /** {@return the constant} */
+        byte byteValue();
