+                                           " gold[" + i + "][" + j + "] = " + g[j] +
+                                           " = " + String.format("%02X", g[j] & 0xFF) +
+                                           " result[" + i + "][" + j + "] = " + r[j] +
+                                           " = " + String.format("%02X", r[j] & 0xFF));
