+  // Convert "~a | ~b" into "~(a & b)"
+  if (is_not(phase, in(1), T_INT) && is_not(phase, in(2), T_INT)) {
+    return make_not(phase,
+		    phase->transform(new AndINode(in(1)->in(1), in(2)->in(1))),
+		    T_INT);
+  }
@@ -859,0 +884,8 @@
