+  // Convert "~a | ~b" into "~(a & b)"
+  if (is_not(phase, in(1), T_LONG) && is_not(phase, in(2), T_LONG)) {
+    return make_not(phase,
+		    phase->transform(new AndLNode(in(1)->in(1), in(2)->in(1))),
+		    T_LONG);
+  }
