+                        /// Lorem ipsum [reference in another comment][##lorem-ipsum].
+                        public void m2() { }
+                    }
+                    """,
+                """
+                    package p;
+                    /// Lorem ipsum [reference in another class][C##lorem-ipsum].
+                    public class D {
+                    }""");
+
+        javadoc("-d", base.resolve("api").toString(),
+                "-Xdoclint:none",
+                "--source-path", src.toString(),
+                "p");
+
+    }
