+    @SuppressWarnings("rawtypes")
+    private final static Collector FIND_FIRST =
+            Collector.<Object,Box<Object>,Optional<Object>>of(
+                    () -> new Box<>(),
+                    (b,e) -> {
+                        if (!b.hasValue) {
+                            b.value = e;
+                            b.hasValue = true;
+                        }
+                    },
+                    (l,r) -> l.hasValue ? l : r,
+                    b -> b.hasValue ? Optional.of(b.value) : Optional.empty()
+            );
