+                            b.value = e;
+                            if (!b.hasValue)
+                                b.hasValue = true;
+                         },
+                         (l,r) -> r.hasValue ? r : l,
+                         b -> b.hasValue ? Optional.of(b.value) : Optional.empty()
+            );
