-    ls = &lsh_info;
+  void after_block_for_safepoint(const char* op_name) {
+    if (_stream != nullptr) {
+      _stream->print_cr("resuming %s: in_use_list stats: ceiling=" SIZE_FORMAT
+                        ", count=" SIZE_FORMAT ", max=" SIZE_FORMAT, op_name,
+                        ceiling(), count(), max());
+      _timer.start();
+    }
