+address StubGenerator::generate_string_indexof() {
+  StubCodeMark mark(this, "StubRoutines", "stringIndexOf");
+  address large_hs_jmp_table[10];   // Jump table for large haystacks
+  address small_hs_jmp_table[10];   // Jump table for small haystacks
+  int jmp_ndx = 0;
+  __ align(CodeEntryAlignment);
+  address start = __ pc();
+  __ enter();  // required for proper stackwalking of RuntimeStub frame
+
+  ////////////////////////////////////////////////////////////////////////////////////////
+  ////////////////////////////////////////////////////////////////////////////////////////
+  //                         AVX2 code
+  ////////////////////////////////////////////////////////////////////////////////////////
+  ////////////////////////////////////////////////////////////////////////////////////////
+  if (VM_Version::supports_avx2()) {  // AVX2 version
+    Label L_begin;
+
+    Label L_0x403852, L_0x403bbd, L_0x403bca, L_0x403844;
+    Label L_0x403b91, L_0x403bd2, L_0x4033c2, L_0x403838, L_0x403733, L_0x403841;
+    Label L_0x40320e, L_0x403215, L_0x403265, L_0x40326f, L_0x403233, L_0x40315a;
+    Label L_0x4032a0, L_0x40386a, L_0x40331c, L_0x403302, L_0x403b38, L_0x403360;
+    Label L_0x4038bd, L_0x4038b5;
+
+    address jump_table;
+    address jump_table_1;
+
+    // Jump past jump table setups to get addresses of cases.
+    __ jmp(L_begin);
+
+    ////////////////////////////////////////////////////////////////////////////////////////
+    ////////////////////////////////////////////////////////////////////////////////////////
+    ////////////////////////////////////////////////////////////////////////////////////////
+    //
+    // Set up jump table entries for both small and large haystack switches.
+
+// Small case 1:
+    // Unroll for speed
+    small_hs_jmp_table[0] = __ pc();
+    {
+      Label L_tmp1, L_tmp2, L_tmp3, L_tmp4, L_tmp5, L_0x403ba5;
+      __ movzbl(rcx, Address(r14, 0));
+      __ movl(rax, rbx);
+      __ andl(rax, 0xf);
+      __ movl(rdx, 0x10);
+      __ subq(rdx, rax);
+      __ cmpq(rdx, rsi);
+      __ movq(rax, rsi);
+      __ cmovl(Assembler::below, rax, rdx);
+      __ testq(rax, rax);
+      __ je_b(L_tmp1);
+      __ xorl(rdi, rdi);
+      __ bind(L_tmp2);
+      __ cmpb(Address(rbx, rdi, Address::times_1), rcx);
+      __ je(L_0x403ba5);
+      __ incq(rdi);
+      __ cmpq(rax, rdi);
+      __ jne(L_tmp2);
+      __ bind(L_tmp1);
+      __ cmpq(rdx, rsi);
+      __ jae(L_0x403852);
+      __ movq(rdi, rsi);
+      __ subq(rdi, rax);
+      __ movq(rdx, rdi);
+      __ andq(rdx, -16);
+      __ je(L_tmp3);
+      __ leaq(r9, Address(rdx, -1));
+      __ movdl(xmm0, rcx);
+      __ vpbroadcastb(xmm0, xmm0, Assembler::AVX_256bit);
+      __ leaq(r10, Address(rbx, rax, Address::times_1));
+      __ xorl(r8, r8);
+      __ bind(L_tmp4);
+      __ vpcmpeqb(xmm1, xmm0, Address(r10, r8, Address::times_1), Assembler::AVX_256bit);
+      __ vpmovmskb(r11, xmm1, Assembler::AVX_256bit);
+      __ testl(r11, r11);
+      __ jne(L_0x403bbd);
+      __ addq(r8, 0x10);
+      __ cmpq(r8, r9);
+      __ jbe(L_tmp4);
+      __ bind(L_tmp3);
+      __ cmpq(rdx, rdi);
+      __ jae(L_0x403852);
+      __ addq(rax, rdx);
+      __ bind(L_tmp5);
+      __ cmpb(Address(rbx, rax, Address::times_1), rcx);
+      __ je(L_0x403bca);
+      __ incq(rax);
+      __ cmpq(rsi, rax);
+      __ jne(L_tmp5);
+      __ jmp(L_0x403852);
+      __ bind(L_0x403ba5);
+      __ movq(rbp, rdi);
+      __ jmp(L_0x403852);
