+      os::free(orig);
+      return;
+    }
+  }
+  // no lower limit found or limit at leaf
+  os::free(cg_path);
+  if (path_iterated) {
+    reader->set_subsystem_path((char*)"/");
+    count = init();
+    if (!cond(count)) {
