+    @IR(counts = {IRNode.SUB_L, "1"})
+    public void notEqualsInvariantSubVariantLong(long inv1, long inv2) {
+        for (int i = 0; i < 500; ++i) {
+            // Reassociate to `inv2 - inv1 != i`
+            if (inv1 - i != inv2) {
