+        protected boolean isAlmostEqual(Color c1, Color c2) {
+            return (Math.abs(c1.getRed() - c2.getRed()) < COLOR_TOLERANCE) &&
+                    (Math.abs(c1.getGreen() - c2.getGreen()) < COLOR_TOLERANCE) &&
+                    (Math.abs(c1.getBlue() - c2.getBlue()) < COLOR_TOLERANCE);
+        }
