+            // Get Graphics from image provider:
+            final Graphics2D g2d = imageProvider.createGraphics();
+            try {
+                paintTest(renderable, g2d, MARKER[idx], true);
+            } finally {
+                g2d.dispose();
+            }
+
+            final long now = System.nanoTime();
+
+            // Update paintIdx:
