-  // lea scratch, lit2
-  // jmp scratch
-
-  // Best case
-  // lea rax, lit1
-  // jmp lit2
-
-  int best = NativeMovConstReg::instruction_size + NativeJump::instruction_size;
-  int worst = 2 * NativeMovConstReg::instruction_size + 3;
-  return MAX2(best, worst);
