+size_t ShenandoahSimpleBitMap::count_leading_ones(ssize_t start_idx) const {
+  assert((start_idx >= 0) && (start_idx < _num_bits), "precondition");
+  size_t array_idx = start_idx >> LogBitsPerWord;
+  uintx element_bits = _bitmap[array_idx];
+  uintx bit_number = start_idx & right_n_bits(LogBitsPerWord);
+  uintx omit_mask = right_n_bits(bit_number);
+  uintx mask = ((uintx) 0 - 1) & ~omit_mask;
+#undef KELVIN_DEBUG
+#ifdef KELVIN_DEBUG
+  printf(" count_leading_ones(" SSIZE_FORMAT "), array_idx: " SIZE_FORMAT ", element_bits: " SIZE_FORMAT_X
+         ", mask: " SIZE_FORMAT_X ", bit_number: " SIZE_FORMAT "\n",
+         start_idx, array_idx, element_bits, mask, bit_number);
+#endif
+  if ((element_bits & mask) == mask) {
+    size_t counted_ones = BitsPerWord - bit_number;
+#ifdef KELVIN_DEBUG
+    printf("  count_leading_ones() recursing after counting " SIZE_FORMAT "\n", counted_ones);
+#endif
+    return counted_ones + count_leading_ones(start_idx - counted_ones);
+  } else {
+    // Return number of consecutive ones starting with the_bit and including more significant bits.
+    uintx aligned = element_bits >> bit_number;
+    uintx complement = ~aligned;;
+#ifdef KELVIN_DEBUG
+    size_t result = count_trailing_zeros<uintx>(complement);
+    printf("  count_leading_ones() aligned: " SIZE_FORMAT_X ", complement: " SIZE_FORMAT_X " returning " SIZE_FORMAT "\n",
+           aligned, complement, result);
+    return result;
+#endif
+    return count_trailing_zeros<uintx>(complement);
+  }
+}
+
+size_t ShenandoahSimpleBitMap::count_trailing_ones(ssize_t last_idx) const {
+  assert((last_idx >= 0) && (last_idx < _num_bits), "precondition");
+  size_t array_idx = last_idx >> LogBitsPerWord;
+  uintx element_bits = _bitmap[array_idx];
+  uintx bit_number = last_idx & right_n_bits(LogBitsPerWord);
+  // All ones from bit 0 to the_bit
+  uintx mask = right_n_bits(bit_number + 1);
+#ifdef KELVIN_DEBUG
+  printf(" count_trailing_ones(" SSIZE_FORMAT "), array_idx: " SIZE_FORMAT ", element_bits: " SIZE_FORMAT_X
+         ", mask: " SIZE_FORMAT_X ", bit_number: " SIZE_FORMAT "\n",
+         last_idx, array_idx, element_bits, mask, bit_number);
+#endif
+  if ((element_bits & mask) == mask) {
+    size_t counted_ones = bit_number + 1;
+#ifdef KELVIN_DEBUG
+    printf("  count_trailing_ones() recursing after counting " SIZE_FORMAT "\n", counted_ones);
+#endif
+    return counted_ones + count_trailing_ones(last_idx - counted_ones);
+  } else {
+    // Return number of consecutive ones starting with the_bit and including less significant bits
+    uintx aligned = element_bits << (BitsPerWord - (bit_number + 1));
+    uintx complement = ~aligned;
+#ifdef KELVIN_DEBUG
+    size_t result = count_leading_zeros<uintx>(complement);
+    printf("  count_trailing_ones() aligned: " SIZE_FORMAT_X ", complement: " SIZE_FORMAT_X " returning " SIZE_FORMAT "\n",
+           aligned, complement, result);
+    return result;
+#endif
+    return count_leading_zeros<uintx>(complement);
+  }
+}
+
+bool ShenandoahSimpleBitMap::is_forward_consecutive_ones(ssize_t start_idx, ssize_t count) const {
+  assert((start_idx >= 0) && (start_idx < _num_bits), "precondition: start_idx: " SSIZE_FORMAT ", count: " SSIZE_FORMAT,
+         start_idx, count);
+  assert(start_idx + count <= (ssize_t) _num_bits, "precondition");
+  size_t array_idx = start_idx >> LogBitsPerWord;
+  uintx bit_number = start_idx & right_n_bits(LogBitsPerWord);
+  uintx element_bits = _bitmap[array_idx];
+  uintx bits_to_examine  = BitsPerWord - bit_number;
+#ifdef KELVIN_DEBUG
+  uintx orig_element_bits = element_bits;
+#endif
+  element_bits >>= bit_number;
+  uintx complement = ~element_bits;
+  uintx trailing_ones;
+  if (complement) {
+    trailing_ones = count_trailing_zeros<uintx>(complement);
+  } else {
+    trailing_ones = bits_to_examine;
+  }
+#ifdef KELVIN_DEBUG
+  printf(" is_forward_consecutive_ones(" SSIZE_FORMAT ", " SSIZE_FORMAT "), orig_element_bits: " SIZE_FORMAT_X
+         ", element_bits: " SIZE_FORMAT_X ", bits_to_examine: " SIZE_FORMAT ", trailing_ones: " SIZE_FORMAT,
+         start_idx, count, orig_element_bits, element_bits, bits_to_examine, trailing_ones);
+#endif
+  if (trailing_ones >= (uintx) count) {
+    return true;
+  } else if (trailing_ones == bits_to_examine) {
+     // Tail recursion
+    return is_forward_consecutive_ones(start_idx + bits_to_examine, count - bits_to_examine);
+  } else {
+    return false;
+  }
+}
+
+bool ShenandoahSimpleBitMap::is_backward_consecutive_ones(ssize_t last_idx, ssize_t count) const {
+  assert((last_idx >= 0) && (last_idx < _num_bits), "precondition");
+  assert(last_idx - count >= -1, "precondition");
+  size_t array_idx = last_idx >> LogBitsPerWord;
+  uintx bit_number = last_idx & right_n_bits(LogBitsPerWord);
+  uintx element_bits = _bitmap[array_idx];
+  uintx bits_to_examine = bit_number + 1;
+#ifdef KELVIN_DEBUG
+  uintx orig_element_bits = element_bits;
+#endif
+  element_bits <<= (BitsPerWord - bits_to_examine);
+  uintx complement = ~element_bits;
+  uintx leading_ones;
+  if (complement) {
+    leading_ones = count_leading_zeros<uintx>(complement);
+  } else {
+    leading_ones = bits_to_examine;
+  }
+#ifdef KELVIN_DEBUG
+  printf(" is_forward_consecutive_ones(" SSIZE_FORMAT ", " SSIZE_FORMAT "), orig_element_bits: " SIZE_FORMAT_X
+         ", element_bits: " SIZE_FORMAT_X ", bits_to_examine: " SIZE_FORMAT ", leading_ones: " SIZE_FORMAT,
+         last_idx, count, orig_element_bits, element_bits, bits_to_examine, leading_ones);
+#endif
+  if (leading_ones >= (uintx) count) {
+    return true;
+  } else if (leading_ones == bits_to_examine) {
+    // Tail recursion
+    return is_backward_consecutive_ones(last_idx - leading_ones, count - leading_ones);
+  } else {
+    return false;
+  }
+}
+
+ssize_t ShenandoahSimpleBitMap::find_next_consecutive_bits(size_t num_bits, ssize_t start_idx, ssize_t boundary_idx) const {
+  assert((start_idx >= 0) && (start_idx < _num_bits), "precondition");
+
+  // Stop looking if there are not num_bits remaining in probe space.
+  ssize_t start_boundary = boundary_idx - num_bits;
+  if (start_idx > start_boundary) {
+    return boundary_idx;
+  }
+  uintx array_idx = start_idx >> LogBitsPerWord;
+  uintx bit_number = start_idx & right_n_bits(LogBitsPerWord);
+  uintx element_bits = _bitmap[array_idx];
+#ifdef KELVIN_DEBUG
+  uintx orig_element_bits = element_bits;
+#endif
+  if (bit_number > 0) {
+    uintx mask_out = right_n_bits(bit_number);
+    element_bits &= ~mask_out;
+  }
+#ifdef KELVIN_DEBUG
+  printf("find_next_consecutive_bits(" SIZE_FORMAT ", " SSIZE_FORMAT ", " SSIZE_FORMAT "), orig_bits: " SIZE_FORMAT_X
+         ", bits: " SIZE_FORMAT_X ", array_idx: " SIZE_FORMAT ", bit_number: " SIZE_FORMAT "\n",
+         num_bits, start_idx, boundary_idx, orig_element_bits, element_bits, array_idx, bit_number);
+#endif
+  while (true) {
+    if (!element_bits) {
+      // move to the next element
+      start_idx += BitsPerWord - bit_number;
+      if (start_idx > start_boundary) {
+        // No match found.
+        return boundary_idx;
+      }
+      array_idx++;
+      bit_number = 0;
+      element_bits = _bitmap[array_idx];
+#ifdef KELVIN_DEBUG
+      printf(" find_next_consecutive_bits() move to next element, "
+             "bits: " SIZE_FORMAT_X ", array_idx: " SIZE_FORMAT ", start_idx: " SSIZE_FORMAT ", bit_number: " SIZE_FORMAT "\n",
+             element_bits, array_idx, start_idx, bit_number);
+#endif
+    } else if (is_forward_consecutive_ones(start_idx, num_bits)) {
+#ifdef KELVIN_DEBUG
+      printf(" find_next_consecutive_bits() found consecutive ones, returning " SSIZE_FORMAT "\n", start_idx);
+#endif
+      return start_idx;
+    } else {
+      // There is at least one non-zero bit within the masked element_bits.  Find it.
+      uintx next_set_bit = count_trailing_zeros<uintx>(element_bits);
+      uintx next_start_candidate_1 = (array_idx << LogBitsPerWord) + next_set_bit;
+
+      // There is at least one zero bit in this span.  Align the next probe at the start of trailing ones for probed span.
+      size_t trailing_ones = count_trailing_ones(start_idx + num_bits - 1);
+      uintx next_start_candidate_2 = start_idx + num_bits - trailing_ones;
+
+      start_idx = MAX2(next_start_candidate_1, next_start_candidate_2);
+      if (start_idx > start_boundary) {
+        // No match found.
+        return boundary_idx;
+      }
+      array_idx = start_idx >> LogBitsPerWord;
+      element_bits = _bitmap[array_idx];
+      bit_number = start_idx & right_n_bits(LogBitsPerWord);
+#ifdef KELVIN_DEBUG
+      uintx orig_element_bits = element_bits;
+#endif
+      if (bit_number > 0) {
+        size_t mask_out = right_n_bits(bit_number);
+        element_bits &= ~mask_out;
+      }
+#ifdef KELVIN_DEBUG
+      printf(" find_next_consecutive_bits() shifting focus to "
+             "original bits: " SIZE_FORMAT_X ", bits: " SIZE_FORMAT_X ", array_idx: " SIZE_FORMAT ", start_idx: " SSIZE_FORMAT
+             ", bit_number: " SIZE_FORMAT "\n",
+             orig_element_bits, element_bits, array_idx, start_idx, bit_number);
+#endif
+    }
+  }
+}
+
+ssize_t ShenandoahSimpleBitMap::find_prev_consecutive_bits(
+  const size_t num_bits, ssize_t last_idx, const ssize_t boundary_idx) const {
+  assert((last_idx >= 0) && (last_idx < _num_bits), "precondition");
+
+  // Stop looking if there are not num_bits remaining in probe space.
+  ssize_t last_boundary = boundary_idx + num_bits;
+  if (last_idx < last_boundary) {
+    return boundary_idx;
+  }
+
+  size_t array_idx = last_idx >> LogBitsPerWord;
+  uintx bit_number = last_idx & right_n_bits(LogBitsPerWord);
+  uintx element_bits = _bitmap[array_idx];
+#ifdef KELVIN_DEBUG
+  uintx orig_element_bits = element_bits;
+#endif
+  if (bit_number < BitsPerWord - 1) {
+    uintx mask_in = right_n_bits(bit_number + 1);
+    element_bits &= mask_in;
+  }
+#ifdef KELVIN_DEBUG
+  printf("find_prev_consecutive_bits(" SIZE_FORMAT ", " SSIZE_FORMAT ", " SSIZE_FORMAT "), orig_bits: " SIZE_FORMAT_X
+         ", bits: " SIZE_FORMAT_X ", array_idx: " SIZE_FORMAT ", bit_number: " SIZE_FORMAT "\n",
+         num_bits, last_idx, boundary_idx, orig_element_bits, element_bits, array_idx, bit_number);
+#endif
+  while (true) {
+    if (!element_bits) {
+      // move to the previous element
+      last_idx -= bit_number + 1;
+      if (last_idx < last_boundary) {
+        // No match found.
+        return boundary_idx;
+      }
+      array_idx--;
+      bit_number = BitsPerWord - 1;
+      element_bits = _bitmap[array_idx];
+#ifdef KELVIN_DEBUG
+      printf(" find_prev_consecutive_bits() move to prev element, "
+             "bits: " SIZE_FORMAT_X ", array_idx: " SIZE_FORMAT ", start_idx: " SSIZE_FORMAT ", bit_number: " SIZE_FORMAT "\n",
+             element_bits, array_idx, last_idx, bit_number);
+#endif
+    } else if (is_backward_consecutive_ones(last_idx, num_bits)) {
+#ifdef KELVIN_DEBUG
+      printf(" find_prev_consecutive_bits() found consecutive ones, returning " SSIZE_FORMAT "\n", last_idx);
+#endif
+      return last_idx + 1 - num_bits;
+    } else {
+      // There is at least one non-zero bit within the masked element_bits.  Find it.
+      uintx next_set_bit = BitsPerWord - (1 + count_leading_zeros<uintx>(element_bits));
+      uintx next_last_candidate_1 = (array_idx << LogBitsPerWord) + next_set_bit;
+
+      // There is at least one zero bit in this span.  Align the next probe at the end of leading ones for probed span.
+      size_t leading_ones = count_leading_ones(last_idx - (num_bits - 1));
+      uintx next_last_candidate_2 = last_idx - (num_bits - leading_ones);
+
+      last_idx = MIN2(next_last_candidate_1, next_last_candidate_2);
+      if (last_idx < last_boundary) {
+        // No match found.
+        return boundary_idx;
+      }
+      array_idx = last_idx >> LogBitsPerWord;
+      bit_number = last_idx & right_n_bits(LogBitsPerWord);
+      element_bits = _bitmap[array_idx];
+#ifdef KELVIN_DEBUG
+      uintx orig_element_bits = element_bits;
+#endif
+      if (bit_number < BitsPerWord - 1){
+        size_t mask_in = right_n_bits(bit_number + 1);
+        element_bits &= mask_in;
+      }
+#ifdef KELVIN_DEBUG
+      printf(" find_prev_consecutive_bits() shifting focus to "
+             "original bits: " SIZE_FORMAT_X ", bits: " SIZE_FORMAT_X ", array_idx: " SIZE_FORMAT ", last_idx: " SSIZE_FORMAT
+             ", bit_number: " SIZE_FORMAT "\n",
+             orig_element_bits, element_bits, array_idx, last_idx, bit_number);
+#endif
+    }
+  }
+}
+
+void ShenandoahRegionPartitions::dump_bitmap_all() const {
+  log_info(gc)("Mutator range [" SSIZE_FORMAT ", " SSIZE_FORMAT "], Collector range [" SSIZE_FORMAT ", " SSIZE_FORMAT "]",
+               _leftmosts[Mutator], _rightmosts[Mutator], _leftmosts[Collector], _rightmosts[Collector]);
+  log_info(gc)("Empty Mutator range [" SSIZE_FORMAT ", " SSIZE_FORMAT
+               "], Empty Collector range [" SSIZE_FORMAT ", " SSIZE_FORMAT "]",
+               _leftmosts_empty[Mutator], _rightmosts_empty[Mutator],
+               _leftmosts_empty[Collector], _rightmosts_empty[Collector]);
+
+#ifdef _LP64
+  log_info(gc)("%6s: %18s %18s %18s", "index", "Mutator Bits", "Collector Bits", "NotFree Bits");
+#else
+  log_info(gc)("%6s: %10s %10s %10s", "index", "Mutator Bits", "Collector Bits", "NotFree Bits");
+#endif
+  dump_bitmap_range(0, _max-1);
+}
+
+void ShenandoahRegionPartitions::dump_bitmap_range(ssize_t start_idx, ssize_t end_idx) const {
+  assert((start_idx >= 0) && (start_idx < (ssize_t) _max), "precondition");
+  assert((end_idx >= 0) && (end_idx < (ssize_t) _max), "precondition");
+  ssize_t aligned_start = _membership[Mutator].aligned_index(start_idx);
+  ssize_t aligned_end = _membership[Mutator].aligned_index(end_idx);
+  ssize_t alignment = _membership[Mutator].alignment();
+  while (aligned_start <= aligned_end) {
+    dump_bitmap_row(aligned_start);
+    aligned_start += alignment;
+  }
+}
+
+void ShenandoahRegionPartitions::dump_bitmap_row(ssize_t idx) const {
+  assert((idx >= 0) && (idx < (ssize_t) _max), "precondition");
+  ssize_t aligned_idx = _membership[Mutator].aligned_index(idx);
+  uintx mutator_bits = _membership[Mutator].bits_at(aligned_idx);
+  uintx collector_bits = _membership[Collector].bits_at(aligned_idx);
+  uintx free_bits = mutator_bits | collector_bits;
+  uintx notfree_bits =  ~free_bits;
+  log_info(gc)(SSIZE_FORMAT_W(6) ": " SIZE_FORMAT_X_0 " 0x" SIZE_FORMAT_X_0 " 0x" SIZE_FORMAT_X_0,
+               aligned_idx, mutator_bits, collector_bits, notfree_bits);
+}
