@@ -125,0 +125,11 @@
+    public HeapRegion heapRegionForAddress(Address addr) {
+        Iterator<HeapRegion> iter = heapRegionIterator();
+        while (iter.hasNext()) {
+            HeapRegion hr = iter.next();
+            if (hr.isInRegion(addr)) {
+                return hr;
+            }
+        }
+        return null;
+    }
+

