-            if (h == null || h.length < 128) {
-                throw new RuntimeException("Test failed: invalid sRGB header");
-            }
-
-            final byte[] lcmsID = new byte[] {
-                (byte)0x6c, // l
-                (byte)0x63, // c
-                (byte)0x6d, // m
