+        bytes = ClassFile.of().build(ClassDesc.of("HasFinal"),
+                    clb -> clb
+                            .withVersion(JAVA_7_VERSION, 0)
+                            .withFlags(ACC_PUBLIC + ACC_SUPER)
+                            .withSuperclass(CD_Object)
+                            .withMethod(INIT_NAME, MTD_void, ACC_PUBLIC,
+                                    mb -> mb.withCode(
+                                            cob -> cob
+                                                    .aload(0)
+                                                    .invokespecial(CD_Object, INIT_NAME, MTD_void)
+                                                    .return_()))
+                            .withMethod("m", MethodTypeDesc.of(CD_Void, CD_String), ACC_PUBLIC + ACC_FINAL,
+                                    mb -> mb.withCode(CodeBuilder::return_))
+                );
