+                                    .withSuperclass(CD_Object)
+                                    .withMethod(INIT_NAME, MTD_void, ACC_PUBLIC,
+                                            mb -> mb.withCode(
+                                                    cob -> cob
+                                                            .aload(0)
+                                                            .invokespecial(CD_Object, INIT_NAME, MTD_void)
+                                                            .return_()))
+                                    .withMethod("m", MethodTypeDesc.of(CD_int), ACC_FINAL + ACC_STATIC,
+                                            mb -> mb.withCode(
+                                                    cob -> cob.ldc(FAILED)
+                                                            .ireturn()))
+                    );
