@@ -149,0 +157,19 @@
+
+  void test_find() {
+    struct Empty {};
+    TreapCHeap<float, Empty, FCmp> treap;
+    using Node = TreapCHeap<float, Empty, FCmp>::TreapNode;
+
+    Node* n = nullptr;
+    auto test = [&](float f) {
+      EXPECT_EQ(nullptr, treap.find(treap._root, f));
+      treap.upsert(f, Empty{});
+      Node* n = treap.find(treap._root, f);
+      EXPECT_NE(nullptr, n);
+      EXPECT_EQ(f, n->key());
+    };
+
+    test(1.0f);
+    test(5.0f);
+    test(0.0f);
+  }
