+TEST_VM_F(TreapTest, TestClosestLeq) {
+  using Node = TreapCHeap<int, int, Cmp>::TreapNode;
+  {
+    TreapCHeap<int, int, Cmp> treap;
+    Node* n = treap.closest_leq(0);
+    EXPECT_EQ(nullptr, n);
+
+    treap.upsert(0, 0);
+    n = treap.closest_leq(0);
+    EXPECT_EQ(0, n->val());
+
+    treap.upsert(-1, -1);
+    n = treap.closest_leq(0);
+    EXPECT_EQ(0, n->val());
+
+    n = treap.closest_leq(6);
+    EXPECT_EQ(6, n->val());
+
+    n = treap.closest_leq(-2);
+    EXPECT_EQ(nullptr, n);
+  }
+}
