+        Asserts.assertThrows(IllegalArgumentException.class,
+                () -> HPKEParameterSpec.of(0, -1, 0));
+        Asserts.assertThrows(IllegalArgumentException.class,
+                () -> HPKEParameterSpec.of(0, 0, -1));
+        Asserts.assertThrows(IllegalArgumentException.class,
+                () -> HPKEParameterSpec.of(65536, 0, 0));
+        Asserts.assertThrows(IllegalArgumentException.class,
+                () -> HPKEParameterSpec.of(0, 65536, 0));
+        Asserts.assertThrows(IllegalArgumentException.class,
+                () -> HPKEParameterSpec.of(0, 0, 65536));
