+        // Cannot init without algorithm identifiers
+        Asserts.assertThrows(InvalidKeyException.class,
+                () -> c2.init(Cipher.ENCRYPT_MODE, kp.getPublic()));
+        Asserts.assertThrows(InvalidKeyException.class,
+                () -> c2.init(Cipher.DECRYPT_MODE, kp.getPrivate()));
