+                    @Override
+                    public int size() {
+                        return MapN.this.size;
+                    }
+
+                    @Override
+                    public Iterator<V> iterator() {
+                        return new ValueIterator();
+                    }
+                };
+                values = vals;
+            }
+            return vals;
+        }
