@@ -6601,0 +6601,21 @@
+// Combined Multiply-Add Shorts into Integer (dst = src1 * src2 + src3 * src4)
+// It's not necessary to have this one, as there is no instructions on riscv to do a
+// scalar muladd. But to enable MulAddVS2VI instrinsic, we need the scalar version first.
+
+instruct muladdS2I(iRegINoSp dst, iRegIorL2I src1, iRegIorL2I src2, iRegIorL2I src3, iRegIorL2I src4, iRegLNoSp tmp) %{
+  match(Set dst (MulAddS2I (Binary src1 src2) (Binary src3 src4)));
+  ins_cost(IMUL_COST * 2);
+  effect(TEMP_DEF dst, TEMP tmp);
+
+  format %{ "mulw  rscratch1, $src1, $src2\n\t"
+            "maddw $dst, $src3, $src4, rscratch1" %}
+
+  ins_encode %{
+    __ mul(t0, as_Register($src1$$reg), as_Register($src2$$reg));
+    __ mul(as_Register($tmp$$reg), as_Register($src3$$reg), as_Register($src4$$reg));
+    __ add(as_Register($dst$$reg), t0, as_Register($tmp$$reg));
+  %}
+
+  ins_pipe(imul_reg_reg);
+%}
+

@@ -877,0 +877,26 @@
+// MulAddVS2VI
+// Vector Multiply-Add Shorts into Integer
+// s1[0]*s2[0] + s1[1]*s2[1] => int[0]
+
+instruct vmuladdS2I(vReg dst, vReg src1, vReg src2, vReg tmp1, vReg tmp2, vReg tmp3) %{
+  predicate(Matcher::vector_element_basic_type(n->in(1)) == T_SHORT);
+  match(Set dst (MulAddVS2VI src1 src2));
+  effect(TEMP_DEF dst, TEMP tmp1, TEMP tmp2, TEMP tmp3);
+  format %{ "vmuladdS2I $dst, $src1, $src2\t" %}
+  ins_encode %{
+    __ vsetvli_helper(T_INT, Matcher::vector_length(this));
+
+    __ vsll_vi(as_VectorRegister($dst$$reg), as_VectorRegister($src1$$reg), 16);
+    __ vsra_vi(as_VectorRegister($dst$$reg), as_VectorRegister($dst$$reg), 16);
+    __ vsra_vi(as_VectorRegister($tmp1$$reg), as_VectorRegister($src1$$reg), 16);
+
+    __ vsll_vi(as_VectorRegister($tmp2$$reg), as_VectorRegister($src2$$reg), 16);
+    __ vsra_vi(as_VectorRegister($tmp2$$reg), as_VectorRegister($tmp2$$reg), 16);
+    __ vsra_vi(as_VectorRegister($tmp3$$reg), as_VectorRegister($src2$$reg), 16);
+
+    __ vmul_vv(as_VectorRegister($dst$$reg), as_VectorRegister($dst$$reg), as_VectorRegister($tmp2$$reg));
+    __ vmacc_vv(as_VectorRegister($dst$$reg), as_VectorRegister($tmp1$$reg), as_VectorRegister($tmp3$$reg));
+  %}
+  ins_pipe(pipe_slow);
+%}
+
