+TEST_VM_F(IndexedFreeListAllocatorTest, FreeingShouldReuseMemory) {
+  using A = IndexedFreeListAllocator<int, mtTest>;
+  A alloc;
+  A::I i = alloc.allocate(1);
+  int* x = &alloc.at(i);
+  alloc.free(i);
+  i = alloc.allocate(1);
+  int* y = &alloc.at(i);
+  EXPECT_EQ(x, y);
+}
