+
+        private BigInteger period() {
+            /*
+             * 0                if i = j = k = 0
+             * (2^i - j) 2^k    otherwise
+             */
+            return i == 0 && j == 0 && k == 0
+                    ? BigInteger.ZERO
+                    : BigInteger.ONE.shiftLeft(i).subtract(BigInteger.valueOf(j)).shiftLeft(k);
+        }
+
+        private int stateBits() {
+            return i == 0 && k == 0 ? Integer.MAX_VALUE : i + k;
+        }
