files.patch:
@@ -1096,1 +1096,11 @@
-      st->print("%s ", _jvm_args_array[i]);
+      // quote any argument containing whitespaces
+      const char* const s = _jvm_args_array[i];
+      bool ws = false;
+      for (const char* p = s; *p != '\0' && !ws; p++) {
+        ws = isspace((unsigned)*p);
+      }
+      if (ws) {
+        st->print("\"%s\" ", s);
+      } else {
+        st->print("%s ", s);
+      }
