+                    final String prefix;
+                    final String localName;
+                    final String[] tokens = name.split(":", 2);
+                    if (tokens.length == 2) {
+                        prefix = tokens[0];
+                        localName = tokens[1];
+                    } else {
+                        localName = name;
+                        switch (args.length) {
+                            case 3 ->
