@@ -251,0 +251,11 @@
+// helper templates to avoid undefined addition/subtraction from nullptr
+template<typename T>
+T* add_to_ptr(T* ptr, uintptr_t val) {
+  return (T*)((uintptr_t)ptr + val * sizeof(T));
+}
+
+template<typename T>
+T* sub_from_ptr(T* ptr, uintptr_t val) {
+  return (T*)((uintptr_t)ptr - val * sizeof(T));
+}
