+package java.text;
+
+/**
+ * Provide the least interfaces that support both:
+ * NumberFormat#format((double|long), StringBuilder, FieldPosition)
+ * and NumberFormat#format(double, StringBuffer, FieldPosition)
+ *
+ */
+interface StringBuilderBufferProxy {
+
+    /**
+     * Return the actual StringBuffer instance
+     *
+     * @return the actual StringBuffer instance
+     */
+    StringBuffer asStringBuffer();
+
+    /**
+     * Return the actual StringBuilder instance.
+     *
+     * @return the actual StringBuilder instance.
+     */
+    StringBuilder asStringBuilder();
+
+    /**
+     * Returns a new {@code String} that contains a subsequence of
+     * characters currently contained in this sequence. The
+     * substring begins at the specified {@code start} and
+     * extends to the character at index {@code end - 1}.
+     *
+     * @param start The beginning index, inclusive.
+     * @param end   The ending index, exclusive.
+     * @return The new string.
+     */
+    String substring(int start, int end);
+
+    /**
+     * Appends the specified string to this character sequence.
+     *
+     * @param str a string.
+     * @return a reference to this object.
+     */
+    StringBuilderBufferProxy append(String str);
+
+    /**
+     * Appends the string representation of the {@code char}
+     * argument to this sequence.
+     *
+     * @param c a {@code char}.
+     * @return a reference to this object.
+     */
+    StringBuilderBufferProxy append(char c);
+
+    /**
+     * Returns the length (character count).
+     *
+     * @return the length of the sequence of characters currently
+     * represented by this object
+     */
+    int length();
+
+    /**
+     * A factory method return an instance with actual implementor
+     * is a StringBuilder instance
+     *
+     * @param sb actual implementor instance
+     * @return an instance that proxy StringBuilder
+     */
+    static StringBuilderBufferProxy of(StringBuilder sb) {
+        return new StringBuilderImpl(sb);
+    }
+
+    /**
+     * A factory method return an instance with actual implementor
+     * is a StringBuffer instance
+     *
+     * @param sb actual implementor instance
+     * @return an instance that proxy StringBuffer
+     */
+    static StringBuilderBufferProxy of(StringBuffer sb) {
+        return new StringBufferImpl(sb);
+    }
+
+    class StringBuilderImpl implements StringBuilderBufferProxy {
+        private final StringBuilder sb;
+
+        public StringBuilderImpl(StringBuilder sb) {
+            this.sb = sb;
+        }
+
+        @Override
+        public int length() {
+            return sb.length();
+        }
+
+        @Override
+        public StringBuffer asStringBuffer() {
+            throw new RuntimeException("Can't cast to StringBuffer");
+        }
+
+        @Override
+        public StringBuilder asStringBuilder() {
+            return sb;
+        }
+
+        @Override
+        public String substring(int start, int end) {
+            return sb.substring(start, end);
+        }
+
+        @Override
+        public StringBuilderBufferProxy append(String str) {
+            sb.append(str);
+            return this;
+        }
+
+        @Override
+        public StringBuilderBufferProxy append(char c) {
+            sb.append(c);
+            return this;
+        }
+    }
+
+    class StringBufferImpl implements StringBuilderBufferProxy {
+
+        private StringBuffer sb;
+
+        public StringBufferImpl(StringBuffer sb) {
+            this.sb = sb;
+        }
+
+        @Override
+        public int length() {
+            return sb.length();
+        }
+
+        @Override
+        public StringBuffer asStringBuffer() {
+            return sb;
+        }
+
+        @Override
+        public StringBuilder asStringBuilder() {
+            throw new RuntimeException("Can't cast to StringBuilder");
+        }
+
+        @Override
+        public String substring(int start, int end) {
+            return sb.substring(start, end);
+        }
+
+        @Override
+        public StringBuilderBufferProxy append(String str) {
+            sb.append(str);
+            return this;
+        }
+
+        @Override
+        public StringBuilderBufferProxy append(char c) {
+            sb.append(c);
+            return this;
+        }
+    }
+}
