+package java.text;
+
+/**
+ * A factory class that create {code StringBuf} instance
+ * that accept {code StringBuffer} and {code StringBuilder}.
+ */
+class StringBufFactory {
+
+    private StringBufFactory() {
+    }
+
+    /**
+     * A factory method return an instance with actual implementor
+     * is a StringBuilder instance
+     *
+     * @param sb actual implementor instance
+     * @return an instance that proxy StringBuilder
+     */
+    static StringBuf of(StringBuilder sb) {
+        return new StringBuilderImpl(sb);
+    }
+
+    /**
+     * A factory method return an instance with actual implementor
+     * is a StringBuffer instance
+     *
+     * @param sb actual implementor instance
+     * @return an instance that proxy StringBuffer
+     */
+    static StringBuf of(StringBuffer sb) {
+        return new StringBufferImpl(sb);
+    }
+
+    static final class StringBuilderImpl implements StringBuf {
+        private final StringBuilder sb;
+
+        public StringBuilderImpl(StringBuilder sb) {
+            this.sb = sb;
+        }
+
+        @Override
+        public int length() {
+            return sb.length();
+        }
+
+        @Override
+        public StringBuffer asStringBuffer() {
+            throw new RuntimeException("Can't cast to StringBuffer");
+        }
+
+        @Override
+        public StringBuilder asStringBuilder() {
+            return sb;
+        }
+
+        @Override
+        public String substring(int start, int end) {
+            return sb.substring(start, end);
+        }
+
+        @Override
+        public StringBuf append(String str) {
+            sb.append(str);
+            return this;
+        }
+
+        @Override
+        public StringBuf append(char c) {
+            sb.append(c);
+            return this;
+        }
+    }
+
+    static final class StringBufferImpl implements StringBuf {
+
+        private StringBuffer sb;
+
+        public StringBufferImpl(StringBuffer sb) {
+            this.sb = sb;
+        }
+
+        @Override
+        public int length() {
+            return sb.length();
+        }
+
+        @Override
+        public StringBuffer asStringBuffer() {
+            return sb;
+        }
+
+        @Override
+        public StringBuilder asStringBuilder() {
+            throw new RuntimeException("Can't cast to StringBuilder");
+        }
+
+        @Override
+        public String substring(int start, int end) {
+            return sb.substring(start, end);
+        }
+
+        @Override
+        public StringBuf append(String str) {
+            sb.append(str);
+            return this;
+        }
+
+        @Override
+        public StringBuf append(char c) {
+            sb.append(c);
+            return this;
+        }
+    }
+}
