@@ -979,0 +981,9 @@
+TEST(os, is_space) {
+  ASSERT_EQ(os::is_space(1024), 0);
+  ASSERT_EQ(os::is_space(256), 0);
+  ASSERT_EQ(os::is_space(-1024), 0);
+  ASSERT_EQ(os::is_space(-42), 0);
+  ASSERT_EQ(os::is_space(EOF), 0);
+  ASSERT_EQ(os::is_space('a'), 0);
+  ASSERT_NE(os::is_space(' '), 0);
+}
