+        /**
+         * Generate InlineCopy-based code. <p>
+         *
+         * The following is an example of the generated target code:
+         *
+         * <blockquote><pre>
+         * import static java.lang.StringConcatHelper.mix;
+         * import static java.lang.StringConcatHelper.prepend;
+         * import static java.lang.StringConcatHelper.newString;
+         * import static java.lang.StringConcatHelper.newArray;
+         *
+         * public static String concat(int arg0, long arg1, boolean arg2, char arg3, String arg4, float arg5, double arg6, Object arg7) {
+         *     String constant0, constant1, ..., constant8;
+         *     int lengthCoder = ...;
+         *
+         *     // String arg
+         *     arg4 = stringOf(arg4);
+         *
+         *     // Types other than byte/short/int/long/boolean/String require a local variable to store
+         *     String str5 = stringOf(arg5);
+         *     String str6 = stringOf(arg6);
+         *     String str7 = stringOf(arg7);
+         *
+         *     lengthCoder = mix(mix(mix(mix(
+         *                   mix(mix(mix(mix(engthCoder,
+         *                       str7), str6), str5), arg4),
+         *                       arg3), arg2), arg1), arg0);
+         *
+         *     String suffix = constant9;
+         *     lengthCoder -= suffix.length();
+         *     byte[] buf = newArray(suffix, lengthCoder);
+         *
+         *     lengthCoder = prepend(prepend(prepend(prepend(
+         *                   prepend(prepend(prepend(prepend(lengthCoder,
+         *                        buf, str7, constant7), buf, str6, constant6),
+         *                        buf, str5, constant5), buf, arg4, constant4),
+         *                        buf, arg3, constant3), buf, arg2, constant2),
+         *                        buf, arg1, constant1), buf, arg0, constant0);
+         *
+         *     return newArray(buf, lengthCoder);
+         * }
+         * </pre></blockquote>
+         */
@@ -1106,4 +1172,1 @@
-                    cb.new_(STRING_BUILDER);
-                    cb.dup();
-
-                    int len = 0;
+                    long initalLengthCoder = JLA.stringConcatInitialCoder();
@@ -1112,1 +1175,1 @@
-                            len += constant.length();
+                            initalLengthCoder = JLA.stringConcatMix(initalLengthCoder, constant);
@@ -1115,11 +1178,55 @@
-                    len += args.parameterCount() * ARGUMENT_SIZE_FACTOR;
-                    cb.loadConstant(len);
-                    cb.invokespecial(STRING_BUILDER, "<init>", INT_CONSTRUCTOR_TYPE);
-
-                    // At this point, we have a blank StringBuilder on stack, fill it in with .append calls.
-                    {
-                        int off = 0;
-                        for (int c = 0; c < args.parameterCount(); c++) {
-                            if (constants[c] != null) {
-                                cb.ldc(constants[c]);
-                                cb.invokevirtual(STRING_BUILDER, "append", APPEND_STRING_TYPE);
+
+                    int paramCount = args.parameterCount();
+
+                    // Compute parameter and local string variable slots
+                    int   paramSlotsTotalSize = 0;
+                    int[] paramSlots          = new int[paramCount];
+                    for (int i = 0; i < paramCount; i++) {
+                        Class<?> cl = args.parameterType(i);
+                        TypeKind kind = TypeKind.from(cl);
+                        paramSlots[i] = paramSlotsTotalSize;
+                        paramSlotsTotalSize += kind.slotSize();
+                    }
+
+                    int lengthCoderSlot = paramSlotsTotalSize;
+                    int bufSlot         = paramSlotsTotalSize + 2;
+
+                    /*
+                     * store string variants:
+                     *
+                     * str0 = Float.toString(args(0));
+                     * str1 = Double.toString(args(1));
+                     * ...
+                     * strN = StringConcatHelper.stringOf(args(N));
+                     *
+                     */
+                    for (int i = 0, strings = 0; i < paramCount; i++) {
+                        Class<?> cl = args.parameterType(i);
+                        TypeKind kind = TypeKind.from(cl);
+                        if (needStringOf(cl)) {
+                            ClassDesc classDesc;
+                            MethodTypeDesc methodTypeDesc;
+                            String methodName;
+                            if (cl == float.class) {
+                                classDesc = CD_Float;
+                                methodName = "toString";
+                                methodTypeDesc = FLOAT_TO_STRING;
+                            } else if (cl == double.class) {
+                                classDesc = CD_Double;
+                                methodName = "toString";
+                                methodTypeDesc = DOUBLE_TO_STRING;
+                            } else {
+                                classDesc = CD_StringConcatHelper;
+                                methodName = "stringOf";
+                                methodTypeDesc = OBJECT_TO_STRING;
+                            }
+
+                            // Types other than byte/short/int/long/boolean/String require a local variable to store
+                            int strLocalSlot = (cl == String.class)
+                                    ? paramSlots[i]
+                                    : bufSlot + (++strings);
+                            cb.loadLocal(kind, paramSlots[i])
+                              .invokestatic(classDesc, methodName, methodTypeDesc)
+                              .astore(strLocalSlot);
+                            if (cl != String.class) {
+                                paramSlots[i] = strLocalSlot;
@@ -1127,6 +1234,0 @@
-                            Class<?> cl = args.parameterType(c);
-                            TypeKind kind = TypeKind.from(cl);
-                            cb.loadLocal(kind, off);
-                            off += kind.slotSize();
-                            MethodTypeDesc desc = getSBAppendDesc(cl);
-                            cb.invokevirtual(STRING_BUILDER, "append", desc);
@@ -1134,3 +1235,82 @@
-                        if (constants[constants.length - 1] != null) {
-                            cb.ldc(constants[constants.length - 1]);
-                            cb.invokevirtual(STRING_BUILDER, "append", APPEND_STRING_TYPE);
+                    }
+
+                    /*
+                     * Store init index :
+                     *
+                     *  lengthCoder = mix(mix(mix(mix(initalIengthCoder, argN), ..., arg2), arg1), 0);
+                     *  ...
+                     *
+                     */
+                    cb.loadConstant(initalLengthCoder);
+                    for (int i = paramCount - 1; i >= 0; i--) {
+                        Class<?> cl = args.parameterType(i);
+                        TypeKind kind = TypeKind.from(cl);
+                        int paramSlot = paramSlots[i];
+
+                        ClassDesc classDesc = CD_StringConcatHelper;
+                        MethodTypeDesc methodTypeDesc;
+                        if (cl == byte.class || cl == short.class || cl == int.class) {
+                            classDesc = CD_Integer;
+                            methodTypeDesc = Mix_int;
+                        } else if (cl == long.class) {
+                            classDesc = CD_Long;
+                            methodTypeDesc = MIX_long;
+                        } else if (cl == boolean.class) {
+                            methodTypeDesc = Mix_boolean;
+                        } else if (cl == char.class) {
+                            methodTypeDesc = Mix_char;
+                        } else {
+                            methodTypeDesc = Mix_String;
+                            kind = TypeKind.from(String.class);
+                        }
+                        cb.loadLocal(kind, paramSlot)
+                          .invokestatic(CD_StringConcatHelper, "mix", methodTypeDesc);
+                    }
+                    cb.lstore(lengthCoderSlot);
+
+                    String suffix = constants[constants.length - 1];
+                    if (suffix == null) {
+                        suffix = "";
+                    }
+                    if (!suffix.isEmpty()) {
+                        cb.lload(lengthCoderSlot)
+                          .ldc((long) suffix.length())
+                          .lsub()
+                          .lstore(lengthCoderSlot);
+                    }
+
+                    /*
+                     * Allocate buffer :
+                     *  buf = StringConcatHelper.newArray(suffix, lengthCoder)
+                     */
+                    cb.ldc(suffix)
+                      .lload(lengthCoderSlot)
+                      .invokestatic(CD_StringConcatHelper, "newArrayWithSuffix", NEW_ARRAY_SUFFIX)
+                      .astore(bufSlot);
+
+                    /*
+                     * prepend arguments :
+                     *  lengthCoder = prepend(prepend(prepend(lengthCoder, buf, arg0), buf, arg1), ...)
+                     */
+                    cb.lload(lengthCoderSlot);
+                    for (int i = paramCount - 1; i >= 0; i--) {
+                        int paramSlot = paramSlots[i];
+                        Class<?> cl = args.parameterType(i);
+                        TypeKind kind = TypeKind.from(cl);
+
+                        MethodTypeDesc methodTypeDesc;
+                        if (cl == byte.class || cl == short.class || cl == int.class) {
+                            methodTypeDesc = PREPEND_int;
+                        } else if (cl == long.class) {
+                            methodTypeDesc = PREPEND_long;
+                        } else if (cl == boolean.class) {
+                            methodTypeDesc = PREPEND_boolean;
+                        } else if (cl == char.class) {
+                            methodTypeDesc = PREPEND_char;
+                        } else {
+                            methodTypeDesc = PREPEND_String;
+                            kind = TypeKind.from(String.class);
+                        }
+                        String constant = constants[i];
+                        if (constant == null) {
+                            constant = "";
@@ -1138,0 +1318,4 @@
+                        cb.aload(bufSlot)
+                          .loadLocal(kind, paramSlot)
+                          .ldc(constant)
+                          .invokestatic(CD_StringConcatHelper, "prepend", methodTypeDesc);
@@ -1139,0 +1323,1 @@
+                    cb.lstore(lengthCoderSlot);
@@ -1140,2 +1325,10 @@
-                    cb.invokevirtual(STRING_BUILDER, "toString", TO_STRING_TYPE);
-                    cb.areturn();
+                    // return StringConcatHelper.newString(buf, lengthCoder));
+                    cb.aload(bufSlot)
+                      .lload(lengthCoderSlot)
+                      .invokestatic(CD_StringConcatHelper, "newString", NEW_STRING)
+                      .areturn();
+                }
+
+                static boolean needStringOf(Class<?> cl) {
+                    return cl != byte.class && cl != short.class   && cl != int.class
+                        && cl != long.class && cl != boolean.class && cl != char.class;
@@ -1156,24 +1349,0 @@
-
-        private static MethodTypeDesc getSBAppendDesc(Class<?> cl) {
-            if (cl.isPrimitive()) {
-                if (cl == Integer.TYPE || cl == Byte.TYPE || cl == Short.TYPE) {
-                    return APPEND_INT_TYPE;
-                } else if (cl == Boolean.TYPE) {
-                    return APPEND_BOOLEAN_TYPE;
-                } else if (cl == Character.TYPE) {
-                    return APPEND_CHAR_TYPE;
-                } else if (cl == Double.TYPE) {
-                    return APPEND_DOUBLE_TYPE;
-                } else if (cl == Float.TYPE) {
-                    return APPEND_FLOAT_TYPE;
-                } else if (cl == Long.TYPE) {
-                    return APPEND_LONG_TYPE;
-                } else {
-                    throw new IllegalStateException("Unhandled primitive StringBuilder.append: " + cl);
-                }
-            } else if (cl == String.class) {
-                return APPEND_STRING_TYPE;
-            } else {
-                return APPEND_OBJECT_TYPE;
-            }
-        }
