+
+                    /*
+                     * prepend arguments :
+                     *  lengthCoder = prepend(prepend(lengthCoder, buf, argN, constantN), ...), buf, arg0, constant0)
+                     */
+                    cb.lload(lengthCoderSlot);
+                    for (int i = paramCount - 1; i >= 0; i--) {
