+        double val = 134.18d;
+        for (int i = 0; i < 4; i++) {
+            if ((i % 2) == 0) {
+                val = Double.NaN;
+            }
+        }
+        return 56.234d % (val % 31.432d);
+    }
+
+    @Test
+    @IR(failOn = {"drem"}, phase = CompilePhase.BEFORE_MATCHING)
+    @IR(counts = {IRNode.CON_D, "1"})
+    public double nanRightConstant() {
+        // Make sure value is only available after second loop opts round
+        double val = 134.18d;
