+                if (c == byte.class) {
+                    mg = MemorySegment.ofArray((byte[])g);
+                    mr = MemorySegment.ofArray((byte[])r);
+                } else if (c == int.class) {
+                    mg = MemorySegment.ofArray((int[])g);
+                    mr = MemorySegment.ofArray((int[])r);
+                } else if (c == long.class) {
+                    mg = MemorySegment.ofArray((long[])g);
