+                mg = (MemorySegment)g;
+                if (!(r instanceof MemorySegment)) {
+                    throw new RuntimeException("verify " + name + ": was not both MemorySegment:" +
+                                           " gold[" + i + "].getClass() = " + g.getClass().getSimpleName() +
+                                           " result[" + i + "].getClass() = " + r.getClass().getSimpleName());
+                }
