+        Asserts.assertEQ("ac", nonFinal("c"));
+        Asserts.assertEQ("ac", nonFluidExtraneousVariable("c"));
+        Asserts.assertEQ("ac", nonFluidConditional("c"));
+        Asserts.assertEQ("aZ", nonFluidOpaqueCall());
