-
-    if ((synth_state & FOCUSED) != 0) {
-        ((GtkObject*)gtk2_widget)->flags |= GTK_HAS_FOCUS;
-    } else {
-        ((GtkObject*)gtk2_widget)->flags &= ~GTK_HAS_FOCUS;
