-                             jvalue* value)
-{
-    jclass  class;
-    jobject result;
-
-    class = (*env)->FindClass(env, class_name);
-    if( class == NULL )
-        return NULL; /* can't find/load the class, exception thrown */
-
-    if( *cid == NULL)
-    {
-        *cid = (*env)->GetMethodID(env, class, "<init>", signature);
-        if( *cid == NULL )
