+void JfrCPUTimeThreadSampling::set_rate_value(double rate, bool autoadapt) {
+  if (_sampler != nullptr) {
+    _sampler->set_rate(rate, autoadapt);
+  }
+  update_run_state(rate, autoadapt);
+}
